package stepByStep;
import java.util.Scanner;

public class No11654 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scan = new Scanner(System.in);
		
        while(scan.hasNext() ) {
        int A = scan.nextInt();
        int B = scan.nextInt();
		System.out.println(A+B);
        }
        scan.close();
	}

}

